import React, { useState, useEffect } from 'react';

function App() {
  const [serverResponse, setResponse] = useState(0)
  const [currentTime, setCurrentTime] = useState(0);

  const requestOptions = {
    method: 'GET'
  }

  useEffect(() => {
    fetch('/getter').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    });
  }, []);

  function handleKeyDown(e) {
    if (e.key === 'Enter') {
      console.log('You pressed enter key');
      setResponse(serverResponse + 1)


      //fetch('/getter', requestOptions)
      //.then(Response => Response.json())
      //.then(serverResponse => setResponse(Response.rithu))
    }
  }

  return (
    <div className="App">
      <p> Your input request to server </p>
      <input
            type="text"
            onKeyDown={handleKeyDown}
      />

      <p> Response from server </p>
      <input
          type="text"
          value={serverResponse}
          readOnly
      />
      <p>The current time is {currentTime}.</p>
    </div>
  );
}

export default App;
